# Other modules from this project
# global variables:
import global_variables as internal_globals
# functions:
from api.action_play import server_communication


async def received_message(message: str, connection):
    print(-1)
    async with connection as websocket:
        print(0)
        get_event_loop().create_task(server_communication(connection, websocket))
        print(1)
        await websocket.send(message)
        print(2)
        await websocket.recv(1024)
        print(3)


